import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getTeacherProfile, getStudentProfile} from '../../actions/profile';
import Spinner from '../layout/Spinner';
import {Link} from 'react-router-dom';
import DashboardTeacher from './DashboardTeacher';

const Dashboard = ({
  getTeacherProfile,
  getStudentProfile,
  auth: { user, accountType },
  profile: { profile, loading }
}) => {
  useEffect(() => {
    if(accountType == 'teacher') getTeacherProfile();
    else getStudentProfile();
  },[getStudentProfile,getTeacherProfile]);
  return loading && profile === null ? <Spinner/> :
  <>
    { accountType == 'student' ?
      <DashboardTeacher user={user} /> :
      <DashboardTeacher user={user} profile={profile} />
    }
  </>
};

Dashboard.propTypes = {
  getTeacherProfile: PropTypes.func.isRequired,
  getStudentProfile: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  profile: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth,
  profile: state.profile
});



export default connect(mapStateToProps, { getTeacherProfile,getStudentProfile })(Dashboard);
