import { apiRequest } from '../actions/apiRequest';
import {
  GET_TASK,
  taskSuccess,
  taskError,
  taskLoading
} from '../actions/tasks';


const student = ({dispatch}) => next => action => {
  next(action);
  if ( action.type === GET_TASK ) {
    const { classId, sectionId } = action.payload;
    dispatch(taskLoading());
    dispatch(apiRequest('GET',`/api/tasks/${classId}/${sectionId}`, taskSuccess, taskError, action.payload, null));
  }
};

export default student;
