import React from "react";
import PropTypes from "prop-types";

const SendSolution = ({
  result,
  taskStatus,
  answers,
  description,
  maxPoints,
  sendSolution,
  resolved,
  error,
  toUpdate,
}) => {
  return (
    <div>
      {resolved ? (
        <>
          {toUpdate ? (
            <>Zadanie wysłano do weryfikacji</>
          ) : (
            <>
              <p className={styles.statusBox + " " + styles.success}>
                Zadanie rozwiazane
              </p>
            </>
          )}
        </>
      ) : (
        <>
          {checkAnswer && (
            <>
              {error.length > 0 && <div className={styles.error}>{error}</div>}
              {correctAnswer == answer ? (
                <div>
                  <p className={styles.statusBox + " " + styles.success}>
                    Dobra odpowiedź
                  </p>
                  <button onClick={() => sendSolution()}>
                    Prześlij rozwiązanie
                  </button>
                </div>
              ) : (
                <div>
                  <p className={styles.error}>Zła odpowiedź</p>
                  <button onClick={() => sendSolution(true)}>
                    Prześlij z prośbą o weryfikacje
                  </button>
                </div>
              )}
            </>
          )}
        </>
      )}
    </div>
  );
};

export default SendSolution;
