import React, { useState } from 'react';
import PropTypes from 'prop-types';

const DisplayPrompts = ({usedPrompts, model, variables}) => {
  const [propmts, addOne] = useState(usedPrompts);
  const [canAddPrompt, cantAddPrompt] = useState(true)
  const addPrompts = () => {
    if(propmts == 1) cantAddPrompt(false);
    if(propmts < 2) addOne(propmts+1);
  }
  return(
    <>
      { canAddPrompt && <button onClick={()=>addPrompts()}>Poka≈º podpowiedz</button>}
      { prompts <= 1 &&
        <ul>
          {
            variables.map(({variable, description}) =>
          <li>{variable+'   '}{description}</li>)
          }
        </ul>
      }
    </>
  )
}

DisplayPrompts.propTypes = {
}

const mapStateToProps = state => ({
});

export default DisplayPrompts;
