import { apiRequest } from '../actions/apiRequest';
import {
  GET_TASK,
  taskSuccess,
  taskError,
  taskLoading
} from '../actions/tasks';


const student = ({dispatch}) => next => action => {
  next(action);
  if ( action.type === GET_TASK ) {
    const { class: {classId}, section } = action.payload;
    dispatch(taskLoading());
    dispatch(apiRequest('GET',`/api/tasks/${cla}`, taskSuccess, taskError, action.payload, null));
  }
};

export default student;
