import React from "react";
import PropTypes from "prop-types";

const SendSolution = ({
  result,
  taskStatus,
  answers,
  description,
  sendSolution,
  resolved,
  error,
  toUpdate,
  checkAnswer,
  setChekAnswers,
}) => {
  return (
    <div>
      {resolved && (
        <>
          {toUpdate ? (
            <>Zadanie wysłano do weryfikacji</>
          ) : (
            <>
              <p>Zadanie rozwiazane</p>
            </>
          )}
        </>
      )}
      {checkAnswer && <>co to kuraw jest</>}
      {!resolved && (
        <>
          {" "}
          {!checkAnswer && (
            <button onClick={() => setChekAnswers(true)}>
              Sprawdź odpowiedź
            </button>
          )}
        </>
      )}
    </div>
  );
};

export default SendSolution;
