import {
  SMALL_LOADER_START,
  SMALL_LOADER_STOP
} from '../actions/smallLoader';

export default function reducer(statePart = [], action = {}) {
  switch (action.type) {
    case SMALL_LOADER_START:
      return{
        ...statePart,
        isFetching: true
      }
    case SMALL_LOADER_STOP:
      return{
        ...statePart,
        data: action.payload,
        isFetching: false
      }
    default:
      return statePart;
  }
}
