import React from 'react';
import { Route, Redirect } from 'react-router-dom';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import BeatLoader from "react-spinners/BeatLoader";

const TeacherRoute = ({ component: Component, auth: { isAuthenticated }, user:{ isFetching, data: { accountType }} , ...rest }) => {
  return (
    <Route {...rest}
    render={props =>{
              if(!isFetching){
                if(isAuthenticated && accountType == 'teacher') return <Component {...props}/>;
                else return <Redirect to='/login' />;
              }
              else <BeatLoader size={50} />
            }
          }
   />
 );
};

TeacherRoute.propTypes = {
  auth: PropTypes.object.isRequired
};

const mapStateToProps = state =>({
  auth: state.auth,
  user: state.user
})

export default connect(mapStateToProps)(TeacherRoute);
