import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import Spinner from '../layout/Spinner';
import './TaskDashboard.css';
const availableClasses = [
  { name: 'Klasy 1', id: 1 },
  { name: 'Klasy 2', id: 2 },
  { name: 'Klasy 3', id: 3 },
  { name: 'Klasy 4', id: 4 },
  { name: 'Klasy 5', id: 5 },
  { name: 'Klasy 6', id: 6 },
  { name: 'Klasy 7', id: 7 },
  { name: 'Klasy 8', id: 8 }
]
const TaskDashboard = () =>{
  const [activeList, setActiveList] = useState(null);
  const [title, setTitle] = useState('Wybierz klase');
  const [activeSection, setActiveSection] = useState(null);

  const hideSections = () =>{
    setActiveList(null);
    setActiveSection(null);
  }
  const showSections = (id) =>{
    setActiveList(id);
    setActiveSection(null);
  }
  return(
    <>
      {false? <Spinner/>:
        <div className='wrapper'>
          <div className='select-class'>
            <h2>Zadania</h2>
            <ul>
              {availableClasses.map(({name, id})=>
                <>
                  <li onClick={()=>{ id == activeList? hideSections() : setActiveList(id)}}>
                    <span>{name}</span>
                  </li>
                  { id == activeList? <ul>
                      <li onClick={()=>setActiveSection(1)}>1</li>
                      <li onClick={()=>setActiveSection(2)}>2</li>
                      <li onClick={()=>setActiveSection(3)}>3</li>
                      <li onClick={()=>setActiveSection(4)}>4</li>
                    </ul> : ''
                  }
                </>
            )}
            </ul>
          </div>
          <div className='students-view bg-dark'>
            <h2>Uczniowie</h2>

          </div>
        </div>
      }
    </>
  )
}

TaskDashboard.propTypes = {

}

const mapStateToProps = state => ({
});

export default connect(mapStateToProps)(TaskDashboard);
