import React, {useState} from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { addClass } from '../../actions/classes';

const AddClass = ({addClass})=>{
  const [formData, setFormData] = useState({
      title: '',
      maxStudentsAmount: ''
  });

  const { title, maxStudentsAmount } = formData;
  const onChange = e => setFormData({...formData, [e.target.name]: e.target.value});
  const onSubmit = (e) =>{
    e.preventDefault();
    addClass(title,maxStudentsAmount);
    setFormData('');
  };
  return (
    <>
      <form className="form" onSubmit={e => onSubmit(e)}>
        <h5>Dodaj klase</h5>
        <div>
          <input
            placeholder="Nazwa klasy"
            name="title"
            value={title}
            onChange={e => onChange(e)}
            required
          />
        </div>
        <div>
          <input
            placeholder="Ilość uczniów"
            type='number'
            name="maxStudentsAmount"
            value={maxStudentsAmount}
            onChange={e => onChange(e)}
            required
          />
        </div>
        <input type="submit" className="btn-primary" value="Dodaj" />
      </form>
    </>
  )
}

AddClass.propTypes = {
}


export default connect(null, {addClass})(AddClass);
