import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import Spinner from '../layout/Spinner';
import {Link} from 'react-router-dom';
import { connect } from 'react-redux';
import { getTeacherProfile } from '../../actions/profile';

const DashboardTeacher = ({user ,profile: { name, invitedByMe, inviter, user: userProfile }}) => {

  const viewProfile = (id) =>{
    console.log(id)
  }
  return (
    <>
      <h1 className="large text-primary">
        Panel nauczyciela
      </h1>
      <p className="lead"><i className="fas fa-user"></i> Witaj {name}</p>
      <h3>Link do twojej grupy:</h3>
      <h5>{`${window.location.origin}/register/${user._id}`}</h5>
      <h4>Zaproszeni przez Ciebie</h4>
      <ul>
        { invitedByMe.length > 0 && invitedByMe.map(({user}) =>(
          <li className="posts" onClick={()=>viewProfile(user._id)}>{user.name}</li>
        ))}
      </ul>
      { inviter !== userProfile ?
        <>
          <h4>Twój szef</h4>
          <h5 onClick={()=>viewProfile(inviter._id)}>{ inviter.name }</h5>
        </> : <h4>Jesteś pierwszy w naszej strukturze</h4>
        }
    </>
)
};

DashboardTeacher.propTypes = {
  profile: PropTypes.object.isRequired,
  user: PropTypes.object.isRequired
};

export default DashboardTeacher;
