import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getClasses } from '../../actions/classes';
import Spinner from '../layout/Spinner';

const Classes = ({getClasses, classes}) =>{
  useEffect(()=>{
    getClasses();
  },[])
  return(
    <>
      {classes == null ? <Spinner/>:
        <>
          <h3>Imię: {profile.name}</h3>
          <h3>Ilość zaproszonych nauczycieli: {profile.invited}</h3>
          <h3>Ilość uczniów: {profile.students}</h3>
          <h3>Ilość klass: {profile.classes}</h3>
        </>
      }
    </>
  )
}

Classes.propTypes = {
  getClasses: PropTypes.func.isRequired
}

const mapStateToProps = state => ({
  classes: state.classes.myClasses
});

export default connect(mapStateToProps, {getClasses})(Classes);
