import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getTeacherProfile, getStudentProfile} from '../../actions/profile';
import Spinner from '../layout/Spinner';
import {Link} from 'react-router-dom';
import DashboardTeacher from './DashboardTeacher';

const Dashboard = ({
  deleteAccount,
  getTeacherProfile,
  getStudentProfile,
  auth: { user, accountType },
  profile: { profile, loading }
}) => {
  useEffect(() => {
    if(accountType == 'teacher') getTeacherProfile();
    else getStudentProfile();
  },[getStudentProfile,getTeacherProfile]);
  return loading && profile === null ? <Spinner/> :
  <>
    <h1 class="large text-primary">
      Panel { accountType == 'student' ? 'ucznia' : 'nauczyciela'}
    </h1>
    <p class="lead"><i class="fas fa-user"></i> Witaj {profile.name}</p>
    <h3>Link do twojej grupy:{`${window.location}`}</h3>
    { accountType == 'student' ? 'ucznia' : <DashboardTeacher profile={profile}/>}
  </>
};

Dashboard.propTypes = {
  getCurrentProfile: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  profile: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth,
  profile: state.profile
});



export default connect(mapStateToProps, { getTeacherProfile,getStudentProfile })(Dashboard);
