import { apiRequest } from '../actions/apiRequest';
import {
  SEND_OPEN_TASK,
  newTaskError
} from '../actions/newTask';

import {
  smallLoadingStart,
  smallLoadingStop
} from '../actions/smallLoading'


const newTask = ({dispatch}) => next => action => {
  next(action);
  if ( action.type === SEND_OPEN_TASK  ) {
    dispatch(smallLoadingStart());
    dispatch(apiRequest('POST','/api/tasks/open', smallLoadingStop, newTaskError, action.payload, null));
  }
};

export default newTask;
