import {
  GET_CLASSES,
  CLASSES_ERROR
} from '../actions/types';

const initialState = {
  myClasses: null,
  loading: true,
  error: false
}

export default function(state = initialState, action) {
  const { type, payload } = action;

  switch (type) {
    case GET_CLASSES:
      return {
        ...state,
        loading: false,
        myClasses: payload
      };
    case CLASSES_ERROR:
      return {
        ...state,
        loading: false,
        error: payload
      };
  }
}
