import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getTeacher } from '../../redux/actions/profile';
import Spinner from '../layout/Spinner';

const TeacherProfile = ({id, teacher}) =>{
  useEffect(()=>{
    getTeacher(id)
  },[])
  return(
    <>
      {profile == null ? <Spinner/>:
        <>
          <h3>{teacher.name}</h3>
          <h3>Ilość zaproszonych nauczycieli: {teacher.invited}</h3>
          <h3>Ilość uczniów: {teacher.students}</h3>
          <h3>Ilość klass: {teacher.classes}</h3>
        </>
      }
    </>
  )
}

TeacherProfile.propTypes = {
}

const mapStateToProps = state => ({
  teacher: state.teacher
});

export default connect(mapStateToProps, {getTeacherProfile})(TeacherProfile);
