import {
  AUTH_LOADING,
  REGISTER_SUCCESS,
  LOGIN_SUCCESS,
  AUTH_ERROR
} from '../actions/auth';

export default function reducer(statePart = [], action = {}) {
  switch (action.type) {
    case AUTH_LOADING:
      return {
        ...statePart,
        isFetching: true,
        errors:[]
      }
    case LOGIN_SUCCESS:
    case REGISTER_SUCCESS:
      localStorage.setItem('token', payload.token);
      return {
        ...statePart,
        isFetching: false,
        isAuthenticated: true,
        token: action.payload.token,
        errors:[]
      }
    case AUTH_ERROR:
      return {
        ...statePart,
        isFetching: false,
        errors: action.payload
      }
    default:
      return statePart;
  }
}
