import React from "react";
import PropTypes from "prop-types";

const SendSolution = ({
  result,
  taskStatus,
  answers,
  description,
  sendSolution,
  resolved,
  error,
  toUpdate,
  checkAnswers,
  setChekAnswers,
}) => {
  return (
    <div>
      {resolved && (
        <>
          {toUpdate ? (
            <>Zadanie wysłano do weryfikacji</>
          ) : (
            <>
              <p>Zadanie rozwiazane</p>
            </>
          )}
        </>
      )}
      {checkAnswers && (
        <>
          {error.length > 0 && <div>{error}</div>}
          {Object.keys(answers).length ? (
            <div>
              <p>Dobra odpowiedź</p>
              <button onClick={() => sendSolution()}>
                Prześlij rozwiązanie
              </button>
            </div>
          ) : (
            <div>
              <button onClick={() => sendSolution()}>Prześlij</button>
              <button onClick={() => sendSolution(true)}>
                Wyślij prośbe o weryfikacje
              </button>
            </div>
          )}
        </>
      )}
      {!resolved && (
        <>
          {" "}
          {!checkAnswers && (
            <button onClick={() => setChekAnswers(true)}>
              Sprawdź odpowiedź
            </button>
          )}
        </>
      )}
    </div>
  );
};

export default SendSolution;
