import React, {useState} from 'react';
import {Link, Redirect} from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { login } from '../../actions/auth';

const Login = ({ login,isAuthenticated }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    accountType: 'teacher'
  });
  const { email,password,accountType } = formData;
  console.log(formData);
  const onChange = e => setFormData({...formData, [e.target.name]: e.target.value});
  const onSubmit = async e =>{
    e.preventDefault();
    login({email,password});
  }

  // Redirect if logen in
  if(isAuthenticated) return <Redirect to='/dashboard' />;

  return(
  <>
    <h1 className="large text-primary">Sign In</h1>
    <p className="lead"><i className="fas fa-user"></i> Sigin Into Your Account</p>
    <form className="form" onSubmit={e => onSubmit(e)}>
      <div className="form-group">
        <input
          type="email"
          placeholder="Email Address"
          name="email"
          value={email}
          onChange={e => onChange(e)}
          required
        />
      </div>
      <div className="form-group">
        <input
          type="password"
          placeholder="Password"
          name="password"
          minLength="6"
          value={password}
          onChange={e => onChange(e)}
          required
        />
      </div>
      <div class="form-group">
        <select onChange={e => onChange(e)} name="status" required>
          <option value="teacher">Nauczyciel</option>
          <option value="student">Uczeń</option>
        </select>
        <small class="form-text"
          >Wybierz rodzaj konta</small
        >
      </div>
      <input type="submit" className="btn btn-primary" value="Login" />
    </form>
    <p className="my-1">
      Don't have account? <Link to='/register'>Sign Up</Link>
    </p>
  </>
);
};

Login.propTypes = {
  login: PropTypes.func.isRequired,
  isAuthenticated: PropTypes.bool
};

const mapStateToProps = state => ({
  isAuthenticated: state.auth.isAuthenticated
});

export default connect(
  mapStateToProps,
  {login}
)(Login);
