    setTitle('');
  }
  return (
    <>
      <form className="form" onSubmit={e => onSubmit(e)}>
        <h5>Dodaj klase</h5>
        <div>
          <input
            placeholder="Nazwa klasy"
            name="title"
            value={title}
            onChange={e => setTitle(e.target.value)}
            required
          />
        </div>
        <div>
          <input
            placeholder="Ilość uczniów"
            type='number'
            name="name"
            value={title}
            onChange={e => setTitle(e.target.value)}
            required
          />
        </div>
        <input type="submit" className="btn-primary" value="Dodaj" />
      </form>
    </>
  )
}

