import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import BeatLoader from "react-spinners/BeatLoader";
import { connect } from 'react-redux';
import { getClasses } from '../../../redux/actions/classes';

const AddTaskToClass = ({getClasses, classes}) =>{
  const {
    isFetching,
    errors,
    data
   } = classes;

  useEffect(()=>{
    getClasses();
  },[]);

  const [ selectedClasses, setSelectedClasses ] = useState(null);

  const setAvailableClasses = () => {
    const newState = [];
    for(let i in data){
      newState.push({title: data[i].title });
    }
  }

  return ( isFetching ? <BeatLoader size={15} /> :
    <>
      <h4>Dodaj zadanie do klasy</h4>
      <div>
        { selectedClasses === null && setAvailableClasses() }
        {data.map(({title,_id})=>
          <>
            <input type="checkbox" name={title} />
            <label for="scales">{title}</label>
          </>
        )
        }
      </div>
    </>
  )
};

const mapStateToProps = state =>({
  classes: state.classes
})

export default connect(mapStateToProps,{getClasses})(AddTaskToClass);
