import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import BeatLoader from "react-spinners/BeatLoader";
import { connect } from 'react-redux';
import { getClasses } from '../../../redux/actions/classes';
import { updateClasses, setTaskId } from '../../../redux/actions/taskToClass';

const SelectClass = ({
  getClasses,
  classes,
  taskId,
  setTaskId,
  updateClasses
}) =>{
  const {
    isFetching,
    errors,
    data
   } = classes;

  useEffect(()=>{
    getClasses();
    setTaskId(taskId);
  },[]);

  const [ selectedClasses, setSelectedClasses ] = useState([]);

  const addClass = (_id) =>{
    if(selectedClasses.includes(_id)){
     setSelectedClasses([...selectedClasses.filter((item) => item !== _id)]);
     updateClasses([...selectedClasses.filter((item) => item !== _id)]);
   }
    else{
      setSelectedClasses([...selectedClasses,_id]);
      updateClasses([...selectedClasses,_id]);
    }
  };

  const addToClasses = () =>{

  };
  return ( isFetching ? <BeatLoader size={15} /> :
    <>
      <h4>Dodaj zadanie do klasy</h4>
      <div>
        {data.map(({title,_id})=>
          <>
            <input type="checkbox" onClick={()=>addClass(_id)} name={title} />
            <label for="scales">{title}</label>
          </>
        )
        }
      </div>
    </>
  )
};

const mapStateToProps = state =>({
  classes: state.classes
})

export default connect(mapStateToProps,
  {
    getClasses,
    updateClasses,
    setTaskId
  })
  (SelectClass);
