import {
  LOADING_CLASSES,
  CLASSES_SUCCESS,
  CLASSES_ERROR
} from '../actions/classes';

export default function reducer(statePart = [], action = {}) {
  switch (action.type) {
    case LOADING_CLASSES:
      return{
        ...statePart,
        isFetching: true
      }
    case CLASSES_SUCCESS:
      return{
        ...statePart,
        data: action.payload,
        isFetching: false
      }
    case CLASSES_ERROR:
      return{
        ...statePart,
        errors: action.payload,
        isFetching: false
      }
    case UPDATE_CLASS:
      return {
        ...state,
        loading: false,
        myClasses: [...state.myClasses.map((item)=>{
          if(item._id == payload.id) item.open = payload.newStatus;
          return item
        })]
      };
    default:
      return statePart;
  }
}
