import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getTeacher } from '../../redux/actions/profile';
import Spinner from '../layout/Spinner';

const TeacherProfile = ({id, teacher: { isFetching} , getTeacher}) =>{
  useEffect(()=>{
    getTeacher(id)
  },[])
  return(
    <>
      {isFetching ? <Spinner/>:
        <>
          <h3>{teacher.name}</h3>
          <h3>Ilość zaproszonych nauczycieli: {teacher.invited}</h3>
          <h3>Ilość uczniów: {teacher.students}</h3>
          <h3>Ilość klass: {teacher.classes}</h3>
        </>
      }
    </>
  )
}

TeacherProfile.propTypes = {
  id: PropTypes.string.isRequired,
  teacher: PropTypes.object.isRequired,
  getTeacher: PropTypes.func.isRequired
}

const mapStateToProps = state => ({
  teacher: state.teacher
});

export default connect(mapStateToProps, {getTeacher})(TeacherProfile);
