import React, { useState } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { setDeadLine } from "../../../redux/actions/taskToClass";
import DateTimePicker from "react-datetime-picker";

const SelectDeadLine = () => {
  const [startDate, setStartDate] = useState(new Date());
  const [rawDate, setRawDate] = useState(new Date());

  return (
    <DatePicker
      selected={startDate}
      onChange={(date) => {
        setStartDate(date);
        setRawDate(date.toISOString());
        setDeadLine(date.toISOString());
      }}
      showTimeSelect
      timeFormat="HH:mm"
      timeIntervals={15}
      timeCaption="time"
      dateFormat="MMMM d, yyyy h:mm aa"
    />
  );
};

export default connect(null, { setDeadLine })(SelectDeadLine);
