import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getClasses, openClass, closeClass } from '../../actions/classes';
import Spinner from '../layout/Spinner';
import AddClass from './AddClass.js';
import StudentsList from './StudentsList.js';
import {Link} from 'react-router-dom';
import './Classes.css';

const Classes = ({getClasses, openClass, closeClass, classes, myId}) =>{
  useEffect(()=>{
    getClasses();
  },[]);

  const [activeClass, setActiveClass] = useState(null);

  return(
    <>
      {classes == null ? <Spinner/>:
        <div className='wrapper'>
          <div className='select-class'>
            <h2>Klasy</h2>
            <ul>
              {classes.map(({title,_id})=><li onClick={()=>{
                setActiveClass(_id)
              }}>{title}</li>)}
            </ul>
            <AddClass />
          </div>
          <div className='students-view bg-dark'>
            <h2>Uczniowie</h2>
            <ul>
              { activeClass == null ? <h5>Wybierz klase</h5> :
                <>
                  {
                    open ?
                    <>
                      <h5>Link do klasy</h5>
                      <h6>{`${window.location.origin}/register/${myId}/${classId}`}</h6>
                      <h6 onClick={()=> closeClass(classId)}>Zamknij klase</h6>
                    </> : <h6 onClick={()=> openClass(classId)}>Otw√≥rz klase</h6>
                  }

                  <StudentsList students={students} />
                </>
              }
            </ul>
          </div>
        </div>
      }
    </>
  )
}

Classes.propTypes = {
  getClasses: PropTypes.func.isRequired
}

const mapStateToProps = state => ({
  classes: state.classes.myClasses,
  myId: state.auth.user._id
});

export default connect(mapStateToProps, {getClasses, openClass, closeClass})(Classes);
