import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import SelectClass from './SelectClass';
import SelectDeadLine from './SelectDeadLine';
import SelectPropmt from './SelectPropmt';

const AddTaskToClass = ({taskId, taskType}) =>{
  return <>
    <SelectClass />
    <SelectDeadLine />
    { taskType === 'openTask' && <SelectPropmt /> }
  </>
}


const mapStateToProps = state => ({
  taskId: state.tasks.data._id,
  taskType: state.tasks.data.taskType
});

export default connect(mapStateToProps)(AddTaskToClass);
