import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getOpenTask } from '../../../redux/actions/tasks';
import BeatLoader from "react-spinners/BeatLoader";

const getRandomIntInclusive = (min, max) => {
  min = Math.ceil(min);
  max = Math.floor(max);
  return Math.floor(Math.random() * (max - min + 1)) + min;
}

const OpenTask = ({match, getOpenTask, tasks: { data, isFetching, errors }}) =>{
  useEffect(()=>{
    getOpenTask(match.params.id);
  },[]);

  const [answer, setAnswer] = useState('');
  const [contentLoaded, setContent] = useState(false);

  const randomGroupIndex = isFetching? '' : getRandomIntInclusive(0,data.data.groups.length);
  let correctAnswer = '';

  const displayContent = (content) =>{
    const { data: { variables, groups } } = data;
    for(let i in variables){
      const currentVar = variables[i].variable;
      const re = new RegExp(`{${currentVar}}`, "g");
      content = content.replace(re, groups[randomGroupIndex][currentVar]);
    }
    correctAnswer = groups[randomGroupIndex].answer;
    setContent(content);
    return <>{content}</>;
  }

  return(
    <>
    {isFetching? <BeatLoader size={20}/> :
      <>
        <h4>{data.name}</h4>
        <p>{contentLoaded? contentLoaded  : displayContent(data.data.content)}</p>
        <input value={answer} onChange={(e)=> setAnswer(e.target.value)}></input>
        <p>{ answer == correctAnswer? "Brawo rozwiązałeś zadanie!" : correctAnswer}</p>
      </>
    }
    </>
  )
}

OpenTask.propTypes = {

}

const mapStateToProps = state =>({
  tasks: state.tasks
})

export default connect(mapStateToProps,{getOpenTask})(OpenTask);
