import {
  LOADING_CLASSES,
  CLASSES_SUCCESS
} from '../actions/classes';

export default function reducer(statePart = [], action = {}) {
  switch (action.type) {
    case LOADING_CLASSES:
      return{
        ...statePart,
        isFetching: true
      }
    case CLASSES_SUCCESS:
      return{
        ...statePart,
        data: action.payload,
        isFetching: false
      }
    case CLASSES_ERROR:
      return{
        ...statePart,
        errors: action.payload,
        isFetching: false
      }
    default:
      return statePart;
  }
}
