import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getOpenTask } from '../../../redux/actions/tasks';

const OpenTask = ({match, getOpenTask, tasks: { data, isFetching, errors }}) =>{
  useEffect(()=>{
    getOpenTask(match.params.id);
  },[])
  return(
    <>
    {isFetching? <BeatLoader size={20}/> :
      <>
        {classId && sectionId ?
          <ul>
            {data.length> 0 ? data.map(({name, taskType, _id})=>
              <li>
                <Link to={`/display/${taskType}/${_id}`}>
                  <span>{name}</span>
                  <span>{showTaskType(taskType)}</span>
                </Link>
              </li>
              ) : 'Nie ma zadań z tego działu'}
          </ul> :
          'Wybierz dział'
        }
      </>
    }
    </>
  )
}

OpenTask.propTypes = {

}

const mapStateToProps = state =>({
  tasks: state.tasks
})

export default connect(mapStateToProps,{getOpenTask})(OpenTask);
