import { apiRequest } from '../actions/apiRequest';
import {
  GET_USER
} from '../actions/user';



const user = ({dispatch}) => next => action => {
  next(action);
  if ( action.type === GET_USER  ) {
    dispatch(apiRequest())
  }
};

export default user;
