import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

const SendTask = ({
  newTask: {
    content,
    variables,
    model,
    groups
  }}) =>{

  const [error, setError] = useState([]);

  const sendTask = () =>{
    setError([]);

    if(!newTask.content.length) setError([...error,{msg: 'Nie podano treśći zadania'}]);
    if(!newTask.variables.length) setError([...error,{msg: 'Nie podano zmiennych'}]);
    if(!newTask.model.length) setError([...error,{msg: 'Nie podano wzoru'}]);
    if(!newTask.groups.length) setError([...error,{msg: 'Nie podano grup'}]);

    let allValuesAdded = false;
    newTask.groups.forEach((item, i) => {
      allValuesAdded = newTask.variables.some(({variable}) => item[variable] == null);
      if(item.answer == null) allValuesAdded = true;
    });
    if(allValuesAdded) setError([...error,{msg: 'Nie uzupełniono wszystkich wartości w grupach'}]);

    if(!error.lenght) console.log('wysyłam');
  }
  console.log(error);
  return(
    <>
      <ul>
        {error.length > 0 && error.map( ({msg}) => <li>{msg}</li> ) }
      </ul>
      <button onClick={()=>sendTask()}>Zapisz zadanie</button>
    </>
  );
};

const mapStateToProps = (state) => ({
  newTask: state.newTask
});

export default connect(mapStateToProps)(SendTask);
