import { apiRequest } from '../actions/apiRequest';
import {
  REGISTER_START,
  REGISTER_SUCCESS,
  AUTH_ERROR,
  authError,
  registerSuccess,
  registerError,
  loading
} from '../actions/auth';

import { setAuthToken } from '../../utils/setAuthToken';

const register = ({dispatch, register}) => next => action => {
  next(action);
  if ( action.type === REGISTER_START  ) {
    const { name, email, password, params } = action.payload;

    const config = {
      headers: {
        'Content-Type' : 'application/json'
      }
    };
    let url = '';
    if(params.class){
       url = `/api/users/student/${params.teacher}/${params.class}`
     } else {
       url = `/api/users/teacher/${params.teacher}`
     }
    dispatch(loading());
    dispatch(apiRequest('POST', url, registerSuccess, registerError, {name, email, password}, config));
  }

  if ( action.type === REGISTER_SUCCESS  ) {
    console.log(action.payload)
  }

  if ( action.type === AUTH_ERROR  ) {
    console.log(action.payload)
  }
};

export default register;
