import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import Spinner from '../layout/Spinner';
import {Link} from 'react-router-dom';
import { connect } from 'react-redux';
import { getProfile } from '../../redux/actions/profile';

const DashboardTeacher = ({ getProfile, user: { name } }) => {
  useEffect(()=>{
    getProfile()
  },[])
  return (
    <>
      <h1 className="large text-primary">
        Panel nauczyciela
      </h1>
      <p className="lead">Witaj {name}</p>
      <h3>Link do twojej grupy:</h3>
      <>
        <h5>{`${window.location.origin}/register/${user._id}`}</h5>
        <h4>Zaproszeni przez Ciebie</h4>
        <ul>
          { invitedByMe.length > 0 && invitedByMe.map(({user}) =>(
            <li className="posts">
              <Link to={`/teacher/${user._id}`}>{user.name}</Link>
            </li>
          ))}
        </ul>
        { user._id !== inviter._id ?
          <>
            <h4>Twój szef</h4>
            <h5>
              <Link to={`/teacher/${inviter._id}`}>{inviter.name}</Link>
            </h5>
          </> : <h4>Jesteś pierwszy w naszej strukturze</h4>
          }
        </>
    </>
  )
};

DashboardTeacher.propTypes = {
  user: PropTypes.object.isRequired,
  getProfile: PropTypes.func.isRequired
};

export default connect(null,{ getProfile })(DashboardTeacher);
