import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { sendOpenTask } from '../../../../redux/actions/newTask';

const SendTask = ({newTask}) =>{
  
  const { content, variables, model, groups, section, class : { classNew }} = newTask;
  const [error, setError] = useState('');

  const sendTask = () =>{

    let allValuesAdded = false;
    groups.forEach((item, i) => {
      allValuesAdded = variables.some(({variable}) => item[variable] == null);
      if(item.answer == null) allValuesAdded = true;
    });

    if(content.trim().length &&
       variables.length &&
       model.trim().length &&
       groups.length &&
       !allValuesAdded &&
       section &&
       classNew
    ){
       setError('');
       sendOpenTask(newTask);
     }
    else setError('Nie uzupełniono wszystkich danych');
  }
  return(
    <>
      {error.length > 0 && error }
      <button onClick={()=>sendTask()}>Zapisz zadanie</button>
    </>
  );
};

const mapStateToProps = (state) => ({
  newTask: state.newTask
});

export default connect(mapStateToProps)(SendTask);
