import axios from 'axios';
import { setAlert } from './alert';
import {
  REGISTER_SUCCESS,
  REGISTER_FAIL,
  USER_LOADED,
  AUTH_ERROR,
  LOGIN_SUCCESS,
  LOGIN_FAIL,
  LOGOUT,
  CLEAR_PROFILE
} from './types';
import setAuthToken from '../utils/setAuthToken';

// Load user

export const loadUser = () => async dispatch => {
  if(localStorage.token){
    setAuthToken(localStorage.token);
  }

  try {
    const res = await axios.get('/api/auth');

    dispatch({
      type: USER_LOADED,
      payload: res.data
    });
  } catch (err){
    dispatch({
      type: AUTH_ERROR
    })
  }
};

// Register user

export const register = ({ name, email, password, params}) => async dispatch =>{
  const config = {
    headers: {
      'Content-Type' : 'application/json'
    }
  }

  const body = JSON.stringify({ name, email, password});
  try {
    let res;
    if(params.class){
      res = await axios.post(`/api/users/teacher/${params.teacher}/${params.class}`, body, config);
    }
    else {
      res = await axios.post(`/api/users/teacher/${params.teacher}`, body, config);
    }

    dispatch({
      type: REGISTER_SUCCESS,
      payload: res.data
    });
    dispatch(loadUser());
  } catch (err) {
    const {errors, msg} = err.response.data;

    if(errors) {
      errors.forEach(error => dispatch(setAlert(error, 'danger')));
    }
    if(msg)dispatch(setAlert(err.response.data.msg, 'danger'));
    dispatch({
      type: REGISTER_FAIL
    })
  }
};

// Login user

export const login = ({email, password, accountType}) => async dispatch =>{
  const config = {
    headers: {
      'Content-Type' : 'application/json'
    }
  }
  const body = JSON.stringify({email, password, accountType});
  console.log(body)
  try {
    const res = await axios.post('/api/auth', body, config);

    dispatch({
      type: LOGIN_SUCCESS,
      payload: res.data
    });
    dispatch(loadUser());
  } catch (err) {
    const errors = err.response.data.errors;

    if(errors) {
      errors.forEach(error => dispatch(setAlert(errors.msg, 'danger')));
    }
    console.log(err.response.data);
    dispatch(setAlert(err.response.data.msg, 'danger'));
    dispatch({
      type: LOGIN_FAIL
    })
  }
};

// LOGOUT

export const logout = () => dispatch => {
  dispatch({type: CLEAR_PROFILE});
  dispatch({type: LOGOUT});
}
