import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import BeatLoader from "react-spinners/BeatLoader";
import MathJax from '../../MathJax';
import { connect } from 'react-redux';
import { getCloseTask } from '../../../../redux/actions/tasks';


const CloseTask = ({
  match,
  getCloseTask,
  accountType,
  tasks: {
    data,
    isFetching,
    errors,
  }
}) =>{

  useEffect(()=>{
    getCloseTask(match.params.id);
  },[]);

   const answers = {};
   const [taskStatus, setTaskStatus] =  useState();


   const prepareState = () => {
     data.data.forEach((item, i) => {
       answers[`answer${i}`] = false;
     });
     setTaskStatus(answers);
   }


  console.log(taskStatus)

  return (
    <>
    {isFetching? <BeatLoader size={20}/> :
      <>
        {prepareState() }
        <h4>{data.name}</h4>
        <ul>{data.data.map(({content},index)=>
              <li>{`${index+1}). `}
                <MathJax content={content} />
                Podaj odpowiedz:<input></input>
              </li>
              )
            }
        </ul>
      </>
    }
    </>
  )
}

CloseTask.propTypes = {

}

const mapStateToProps = state =>({
  tasks: state.tasks,
  accountType: state.user.data.accountType
})

export default connect(mapStateToProps,{getCloseTask})(CloseTask);
