import { apiRequest } from '../actions/apiRequest';
import {
  GET_USER,
  userSuccess,
  userError
} from '../actions/user';

import {
  fetchingStart
} from '../actions/fetching'

const user = ({dispatch}) => next => action => {
  next(action);
  if ( action.type === GET_USER  ) {
    dispatch(apiRequest('GET','/api/auth', userSuccess, userError, null, null));
  }
};

export default user;
