import React, { useState } from "react";
import PropTypes from "prop-types";
import TextareaAutosize from "react-textarea-autosize";

const ReviewTask = ({ taskId, studentId }) => {
  const [message, setMessage] = useState("");
  const [accept, setAccept] = useState(false);
  console.log(accept);
  return (
    <div>
      <h4>Panel weryfikacyjny</h4>
      <h5>Dodaj wiadomość:</h5>
      <TextareaAutosize
        maxCols="15"
        minCols="5"
        maxRows="15"
        minRows="5"
        value={message}
        onChange={() => setMessage()}
      />
      <h4>Wybierz status</h4>
      <select
        value={accept}
        onChange={(e) => {
          setAccept(e.target.value === "true" ? true : false);
        }}
      >
        <option value={true}>Do poprawy</option>
        <option value={false}>Rozwiązane</option>
      </select>
    </div>
  );
};

export default ReviewTask;
