import {
  TASK_LOADING,
  TASK_SUCCESS,
  TASK_ERROR
} from '../actions/tasks';

export default function reducer(statePart = [], action = {}) {
  switch (action.type) {
    case TASK_LOADING:
      return {
        ...statePart,
        isFetching: true
      }
    case TASK_SUCCESS:
      return {
        ...statePart,
        isFetching: false,
        data: action.payload
      }
    case TASK_ERROR:
      return {
        ...statePart,
        isFetching: false,
        errors: action.payload
      }
    default:
      return statePart;
  }
}
