import { API_REQUEST } from '../actions/apiRequest';
import Axios from 'axios';

import {
  fetchingStop
} from '../actions/fetching'

const apiRequest = ({dispatch, getState}) => next => action => {
  next(action);
  if( action.type === API_REQUEST) {
    const { method, url, onSucces, onError, body, config } = action.meta;
    Axios({
      method: method,
      url: url,
      data: body,
      config: config
    })
      .then( ({data}) =>{
        dispatch(onSucces(data));
        dispatch(fetchingStop());
      }
      )
      .catch( ({response}) =>{
        dispatch(onError(response));
        dispatch(fetchingStop());
      }
      );
  }
};

export default apiRequest;
