import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { availableClasses } from '../TaskDashboardConfig.js';

const SelectSection = () => {
  const [currentClass, setCurrentClass] = useState('');
  const [currentSection, setCurrentSection] = useState('');

  const setClass = e =>{
    setCurrentClass(e.target.value);
    setCurrentSection('')
  };

  const setSection = e =>{
    setCurrentSection(e.target.value);
  };

  return (
    <>
      <h5>Wybierz klase i dzia≈Ç</h5>
      <select
        value={currentClass}
        onChange={(e)=>{setClass(e)}}
      >
        <option value={''}>---</option>
        { availableClasses.map(({name, id})=>
          <option value={id}>{name}</option>)
        }
      </select>

      <select
        value={currentSection}
        onChange={(e)=>{setSection(e)}}
      >
        <option value={''}>---</option>
        { currentClass &&
          availableClasses[currentClass-1].sections.map(({name, id})=>
          <option value={id}>{name}</option>)
        }
      </select>

    </>
  )
}

export default connect(null)(SelectSection);
