import React from 'react';
import { Route, Redirect } from 'react-router-dom';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import BeatLoader from "react-spinners/BeatLoader";

const StudentRoute = ({ component: Component, auth: { isAuthenticated, accountType, loading } , ...rest }) => {
  return (
    <Route {...rest}
    render={props =>{
              if(!loading){
                if(isAuthenticated && accountType == 'student') return <Component {...props}/>;
                else return <Redirect to='/login' />;
              }
            }
          }
   />
 );
};

StudentRoute.propTypes = {
  auth: PropTypes.object.isRequired
};

const mapStateToProps = state =>({
  auth: state.auth
})

export default connect(mapStateToProps)(StudentRoute);
