import React, { useState } from "react";
import PropTypes from "prop-types";
import styles from "./DisplayPrompts.module.scss";
import PromptsDumm from "./PromptsDumm";
import MathJax from "../../../MathJax";
import { connect } from "react-redux";
import { usePrompt } from "../../../../../redux/actions/resolveTask";

const DisplayPrompts = ({
  usedPrompts,
  model,
  variables,
  accountType,
  taskId,
  usePrompt,
}) => {
  return (
    <div>
      {accountType == "teacher" && (
        <PromptsDumm model={model} variables={variables} />
      )}
      {accountType == "student" && (
        <div className={styles.root}>
          <button onClick={() => usePrompt(taskId)}>Pokaż podpowiedz</button>
          {usedPrompts >= 1 && (
            <>
              <h4>Zmienne:</h4>

              <ul>
                {variables.map(({ variable, description }, index) => (
                  <li key={index}>
                    {variable + "  -  "}
                    {description}
                  </li>
                ))}
              </ul>
            </>
          )}
          {usedPrompts == 2 && (
            <>
              {" "}
              <h4>Wzór:</h4>
              <MathJax content={"`" + model + "`"} />
            </>
          )}
        </div>
      )}
    </div>
  );
};

DisplayPrompts.propTypes = {};

const mapStateToProps = (state) => ({
  usedPrompts: state.tasks.taskConfig.usedPrompts,
});

export default connect(mapStateToProps, { usePrompt })(DisplayPrompts);
