import {
  LOADING_USER
} from '../actions/user';

export default function reducer(statePart = [], action = {}) {
  switch (action.type) {
    case LOADING_USER:
      return {
        ...statePart,
        isFetching: true
      }
    default:
      return statePart;
  }
}
