import {
  AUTH_LOADING,
  REGISTER_SUCCESS,
  REGISTER_ERROR
} from '../actions/auth';

export default function reducer(statePart = [], action = {}) {
  switch (action.type) {
    case AUTH_LOADING:
      return {
        ...statePart,
        isFetching: true,
        errors:[]
      }
    case REGISTER_SUCCESS:
      return {
        ...statePart,
        isFetching: false,
        isAuthenticated: true,
        token: action.payload.token,
        accountType: action.payload.accountType
      }
    case REGISTER_ERROR:
      return {
        ...statePart,
        isFetching: false,
        errors: action.payload
      }
    default:
      return statePart;
  }
}
