import {
  UPDATE_CLASSES,
  SET_TASK_ID
} from '../actions/taskToClass';

export default function reducer(statePart = [], action = {}) {
  switch (action.type) {
    case UPDATE_CLASSES:
      return {
        ...statePart,
        data: {
          ...statePart.data,
          classes: action.payload
        }
      }

    default:
      return statePart;
  }
}
