import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import Spinner from '../layout/Spinner';
import {Link} from 'react-router-dom';

const DashboardTeacher = ({user ,profile: { name, invitedByMe, inviter }}) => {
  return (
    <>
      <h1 className="large text-primary">
        Panel nauczyciela
      </h1>
      <p className="lead"><i className="fas fa-user"></i> Witaj {name}</p>
      <h3>Link do twojej grupy:</h3>
      <h5>{`${window.location.origin}/register/${user._id}`}</h5>
      <h4>Zaproszeni przez Ciebie</h4>
      <ul>
        { invitedByMe.length > 0 && invitedByMe.map(({name, teacherProfile}) =>(
          <li>{name}<button onClick={()=>console.log(teacherProfile)}>{'Zobacz profil'}</button></li>
        ))}
      </ul>
      { inviter ?
        <>
          <h4>Tw√≥j szef</h4>
          <h5>{ inviter.name }<button onClick={()=>console.log(inviter.user)}>{'Zobacz profil'}</button></h5>
        </> : ''
        }
    </>
)
};

DashboardTeacher.propTypes = {
  profile: PropTypes.object.isRequired,
  user: PropTypes.object.isRequired
};

export default DashboardTeacher;
