import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import SelectClass from './SelectClass';
import SelectDeadLine from './SelectDeadLine';
import SelectPrompt from './SelectPrompt';

const AddTaskToClass = ({taskId, taskType, taskParams}) =>{
  return <>
    <SelectClass />
    <SelectDeadLine />
    { taskType === 'openTask' && <SelectPrompt /> }
  </>
}


const mapStateToProps = state => ({
  taskId: state.tasks.data._id,
  taskType: state.tasks.data.taskType,
  taskParams: state.addTaskToClass.data
});

export default connect(mapStateToProps)(AddTaskToClass);
