import { apiRequest } from '../actions/apiRequest';
import {
  GET_TASK,
  taskSuccess,
  taskError,
  taskLoading
} from '../actions/tasks';


const student = ({dispatch}) => next => action => {
  next(action);
  if ( action.type === GET_TASK ) {
    const body = JSON.stringify({class: action.payload.class, section: action.payload.section});

    dispatch(taskLoading());
    dispatch(apiRequest('GET','/api/tasks', taskSuccess, taskError, body, null));
  }
};

export default student;
